import styled from 'styled-components';

export const BoxBackground = styled.div`
height: 128px;
width: 905px;
border-radius: 15px;
background-color: #FFF;
box-shadow: 0 0 50px 0 rgba(0,0,0,0.2);
margin-left: 60px;
margin-top: 336px;

`;

export const InputField = styled.div`
  height: 64px;
  width: 200px;
  margin-left: 40px;
  margin-top: 28px;

`;

export const Name = styled.div`
  height: 14px;
  width: 43px;
  color: #363A3E;
  font-family: 'Roboto';
  font-size: 12px;
  line-height: 14px;
  margin-left: 8px;
`;

export const Background = styled.div`
  height: 40px;
  width: 200px;
  border-radius: 8px;
  background-color: rgba(54,58,62,0.08);
  margin-top: 10px;  
`;
