import styled from 'styled-components';

export const UpperHalf = styled.div`
  height: 400px;
  width: 100%;
  background-color: #363A3E;
  display: flex;
  img {
    height: 35px;
    width: 99.8px;
    margin-top: 40px;
    margin-left: 60px;
  }
`;

export const Headline = styled.div`
    color: #FFF;
    font-size: 40px;
    margin-top: 199px;
    margin-left: 60px;
    font-weight: bold;
    display: flex;
    position: absolute;

`;
