import styled from 'styled-components';

export const Container = styled.div`
  height: 400px;
  width: 1280px;
  background-color: #363A3E;

  display: flex;
`;

export const Headline = styled.div`
    height: 47px;
    width: 570px;
    color: #FFF;
    font-family: 'Montserrat';
    font-size: 40px;
    font-weight: 700;
    line-height: 47px;
    text-align: center;
`;
