import styled from 'styled-components';

export const Button = styled.div`
button {
  height: 40px;
  width: 117px;
  border-radius: 8px;
  background-color: #FF4F54;
  margin-right: 40px;
  margin-top: 52px; 
}
`;

export const Text = styled.div`
button {

  color: #FFF;
  font-family: "Roboto";
  font-size: 16px;
  font-weight: 700;
  line-height: 19px;
}
`;
